import React, { Component } from "react";
import { Table } from "react-bootstrap";
import Moment from "react-moment";
import API from "../../utils/API";
import "./style.css";

class Employee extends Component {
  state = {
    empData: [],
    currentSort: 'default'
  };

  componentDidMount() {
    API.getEmployees() //("https://randomuser.me/api/?nat=us&results=200")
      .then(res => {
        const empData = res.data.results;
        this.setState({ empData });
      })
      .catch(err => console.log(err));
  }

  onSortChange = () => {
		const { currentSort } = this.state;
		let nextSort;

		if (currentSort === 'down') nextSort = 'up';
		else if (currentSort === 'up') nextSort = 'default';
		else if (currentSort === 'default') nextSort = 'down';

		this.setState({
			currentSort: nextSort
		});
  };

  render() {

    return (

      <div>
        <Table striped bordered hover variant="light" style={{maxWidth: "90%", margin: "auto", textAlign: "center"}}>
          <thead>
            <tr>
              <th>Image<button onClick={this.onSortChange}>
									<i className={`fas fa-${sortTypes[currentSort].class}`} />
								</button></th>
              <th>Name</th>
              <th>Phone</th>
              <th>Email</th>
              <th>DOB</th>
            </tr>
          </thead>
          <tbody>

            {
              this.state.empData.map(data => (

                <tr>
                  <td><img src={data.picture.thumbnail}/></td>
                  <td>{data.name.title}. {data.name.first}, {data.name.last}</td>
                  <td>{data.phone}</td>
                  <td>{data.email}</td>
                  <td><Moment format="MM-DD-YYYY">{data.dob.date}</Moment></td>
                </tr>
              ))
            }
          </tbody>
        </Table>
      </div>

    );
  }


}


export default Employee;
